import React from 'react';
import ReactDOM from 'react-dom';
import {BrowserRouter,Switch,Route} from "react-router-dom";
import Home from "./Home";

/*Path:要渲染的URL，没有Path属性的时候将匹配任意路由
 * Component：要渲染的组件
 * exact: 精确匹配
 * strict： 严格匹配、考虑路径结尾的/
 * */
export default class App extends React.Component{
    constructor(){
        super(...arguments);
        this.now = 0;
        this.state={
            data:[{
                id:0,
                title: "111",
                singer: "111",
                selected: false,
                like: false,
            },{
                id:1,
                title: "222",
                singer: "222",
                selected: false,
                like: false,
            },{
                id:2,
                title: "333",
                singer: "333",
                selected: false,
                like: false,
            }]
        };
        this.add = this.add.bind(this);
        this.setCheckAll = this.setCheckAll.bind(this);
        this.setCheck = this.setCheck.bind(this);
        this.setLike = this.setLike.bind(this);
        this.remove = this.remove.bind(this);
        this.removeSelect = this.removeSelect.bind(this);
        this.likeSelect = this.likeSelect.bind(this);
        this.cancelLikeSelect = this.cancelLikeSelect.bind(this);
    }
    render () {
        console.log(this.state.data);
        return (
        <BrowserRouter>
            <div id="musicApp">
                <Switch>
                 <Route path="/" render={()=>{
                     return <Home
                         data={this.state.data}
                     />
                 }}/>
                </Switch>
            </div>
        </BrowserRouter>
        )
    }
}
ReactDOM.render(<App/>,document.getElementById('root'));
