import React from 'react';
import ReactDOM from 'react-dom';
import store from './store';



/*dispatch(obj) 修改数据*/
store.dispatch({
    type: "ADD",
    title:"title",
    singer:"singer",
})

/*删除数据*/
store.dispatch({
    type: "REMOVE",
    id: store.getState().data[0]['id']
})
console.log('store:',store.getState().counterReducer);

