import React from 'react';
import ReactDOM from 'react-dom';
import {createStore} from 'redux';
import reducer from './reducer';
/*创建一个store*/
let store=createStore(reducer);
/*reducer是一个回调函数，包含两个对象参数，state和action*/

/*dispatch(obj) 修改数据*/
store.dispatch({
    type: "ADD",
    title:"title",
    singer:"singer",
})

/*删除数据*/
store.dispatch({
    type: "REMOVE",
    id: store.getState().data[0]['id']
})


ReactDOM.render (
<div>
<h1>欢迎使用redux!</h1> 
<span>计数器：0</span>
</div>             

