import React from 'react';
import ReactDOM from 'react-dom';
import {createStore} from 'redux';
import reducer from './reducer';
/*创建一个store*/
let store=createStore(reducer);

/*dispatch(obj) 修改数据*/

store.dispatch({
    type: "ADD",
    title:"title",
    singer:"singer",
})
console.store.dispatch({
    type: "REMOVE",
    id: store.getState().data[0]['id']
})


ReactDOM.render (<h1>欢迎使用redux!</h1> , document.getElementById ( 'root' ) );
