import React from 'react';
import ReactDOM from 'react-dom';
import store from './store';



/*dispatch(obj) 修改数据*/
store.dispatch({
    type: "ADD",
    title:"title",
    singer:"singer",
})

/*删除数据*/
store.dispatch({
    type: "REMOVE",
    id: store.getState().data[0]['id']
})

class ReduxPage extends React.Component{
    constructor () {
        super ( ...arguments);
        this.state={
            'counter': store.getState().counterReducer
        };
    }    
   add(){
     store.dispatch({ type: "ADD" });
     console.log('store:',store.getState().counterReducer);
     console.log('store:',this.state);

   }

   /*componentDidMount(){
      store.subscribe(() => {
           this.forceUpdate();  //强制刷新
        });
   }*/
   render(){
       return (
        <div>
          <h3>ReduxPage</h3>
          <p>当前计数器数是：<b style={{color:'red'}}>{this.state.counter}</b></p>
          <div className='counter'>
             <button onClick={this.add}>add</button>
             <button >minus</button>
          </div>
        </div>
       )
   }
}
ReactDOM.render (
<div>
<h1>欢迎使用redux!</h1> 
<hr/>

<ReduxPage/>
</div> ,document.getElementById ( 'root' ) );
