import {combineReducers} from "redux";


let data = ( data = [], action )=>{
    switch (action.type){
       case "ADD":
            return [...data,{

                title: action.title,
                singer:action.singer,
            }]
    }
    return data;
}
let reducer= combineReducers({data});

export default reducer;
