import React from 'react';
import ReactDOM from 'react-dom';

/*创建一个store*/
let store=createStore(reducer);
/*reducer是一个回调函数，包含两个对象参数，state和action*/

/*dispatch(obj) 修改数据*/
store.dispatch({
    type: "ADD",
    title:"title",
    singer:"singer",
})

/*删除数据*/
store.dispatch({
    type: "REMOVE",
    id: store.getState().data[0]['id']
})
console.log('store:',store.getState()).counterReducer;
class ReduxPage extends React.Component{

   render(){
       return (
        <div>
          <h3>ReduxPage</h3>
          <span>store</span>
          <button >add</button>
          <button >minus</button>
        </div>
       )
   }
}
ReactDOM.render (
<div>
<h1>欢迎使用redux!</h1> 
<hr/>

<ReduxPage/>
</div> ,document.getElementById ( 'root' ) );
